custom.scss:
/* Custom Code from ThemeValve */
    @media(min-width:992px){
        .productView-details-3{
        display: inline-block;
            width: 100%;  
        }
        .product-layout-2 .productView-details-wrap{
            margin-bottom: 10px;
        }
    }

    .product-layout-2 #completeTheLook{
        padding-top: 0;
    }

    #completeTheLook .productGrid .product .card .card-title .color-name{
        display: none;
    }

    #product-listing-container{
        position: relative;
    }

/* Change text selection color */
    ::-moz-selection { /* Code for Firefox */
        color: 	#FFFFFF;
        background: #53625A;
    }
            
    ::selection {
        color: 	#FFFFFF;
        background: #53625A;
    }

/* Remove capitalization */
    h1, h2, h3, h4, h5, h6, strong, span, a, label, #sort, div.pagination-info {
        text-transform: none !important;
    }

    dt.productView-info-name {
        text-transform: none !important;
    }

/* Change Shogun Accordion */
	.shogun-accordion-title {text-transform: none}

/* Mailchimp Form Design */
    #mc_embed_signup form {
        text-align:center; padding:10px 0 10px 0;
    }

    /* position input field horizontally */
    .mc-field-group { 
        display: inline-block; 
    } 

    #mc_embed_signup input.email {
        font-family:"Sarabun","Google_Sarabun","Open Sans","Helvetica Neue",Arial,Helvetica,Verdana;
        font-style:300,sans-serif;
        font-size: 18px; 
        border: 1px solid #ABB0B2;
        -webkit-border-radius: 0px;
        -moz-border-radius: 0px;
        border-radius: 0px;
        color: #53625A;
        background-color: #fff;
        box-sizing:border-box;
        height:34px;
        padding: 0px 0.4em;
        display: block;
        margin: 10px 20px 10px 0;
        width:550px;
        vertical-align:top;
    }

    #mc_embed_signup .clear {
        display:inline-block;
    }

    #mc_embed_signup .button {
        font-size: 18px;  
        padding:13px 25px; 
        display: inline-block; 
        background-color: #F2CA80; 
        border: 2px solid Transparent;
    }

    #mc_embed_signup .button:hover {
        border-color: #53625A;
    }

    @media screen and (max-width: 767px) {
            .mc-field-group {
                display: block;
            }
            #mc_embed_signup input.email {
                width:100%;
            }
    }

/* Product Page Custom Sections */
    .product-detail-side {
        padding: 20px 8% 20px 8%;
        margin:	0 0 0 0;
    }

    .text-detail-side {
        font-size: 20px;
        font-family: Sarabun;
        font-weight: 300;
        color: #8bb192;
    }

    .text-main-slogan {
        font-family: 'IBM Plex Serif';
        font-size: 40px;
        font-weight: 300;
        color: #F5F8F6;
        text-align: center;
    }

    .text-main-slogan-light {
        font-family: 'IBM Plex Serif';
        font-size: 40px;
        font-weight: 300;
        color: #53625A;
        text-align: center;
    }

/* Product Release Date Design updates */
    .product-release-date-box {
        font-family: $header-font-family;
        font-weight: 600;
        font-size: 14px;
        margin-bottom: 2.5rem;

        .product-release-date-text {
            background: #f2ca80;
            border: 1px solid #f2ca80;
            padding: 5px 10px;
            color: stencilColor('custom-badge-color');
            background: stencilColor('custom-badge-bgColor');
            border: 1px solid stencilColor('custom-badge-border-color');
            display: table-cell;
        }
    }
        
