  /* FAQ Section */
  .faq-container {
      font-family: "PP Neue Montreal";
      max-width: 100%;
      padding: 0 16px;
      box-sizing: border-box;
  }
  .faq-item {
      border-bottom: 1px solid #0F0F0F;
      padding: 16px 0;
  }
  .faq-question {
      display: flex;
      justify-content: space-between;
      align-items: flex-start;
      cursor: pointer;
      font-weight: 500;
      font-size: 16px;
      transition: all 0.2s ease;
      color: #0F0F0F;
      padding-right: 30px;
      position: relative;
  }
  .faq-question::after {
      content: "";
      position: absolute;
      right: 5px;
      top: 8px;
      width: 8px;
      height: 8px;
      border-right: 2px solid #0F0F0F;
      border-bottom: 2px solid #0F0F0F;
      transform: rotate(45deg);
      transition: transform 0.2s ease;
  }
  .faq-item.active {
      border-bottom-color: #0F0F0F;
  }
  .faq-item.active .faq-question {
      color: #0F0F0F;
  }
  .faq-item.active .faq-question::after {
      transform: rotate(225deg);
      border-color: #EB6E38;
  }
  .faq-answer {
      max-height: 0;
      overflow: hidden;
      transition: max-height 0.3s ease;
      font-size: 14px;
  }
  .faq-answer.active {
      max-height: 1000px;
  }
  .faq-answer-content {
      padding: 20px 0 12px 0;
      opacity: 0;
      transition: opacity 0.2s ease;
  }
  .faq-answer.active .faq-answer-content {
      opacity: 1;
  }

  /* Responsive styles */
  @media (max-width: 768px) {
      .faq-question {
          font-size: 14px;
          line-height: 1.4;
      }
      .faq-answer-content {
          font-size: 14px;
          line-height: 1.5;
          padding: 15px 0 10px 0;
      }
  }

  @media (max-width: 480px) {
      .faq-container {
          padding: 0 10px 0 0;
