                        display: flex;
                        gap: 0;
                        margin: 0;
                        padding: 0;
                        width: 100%;
                        max-width: 76px;
                        flex-wrap: nowrap;
                        align-items: center;
                        border-radius: 50px;
                        background-color: #EBF2F0;
                        justify-content: space-between;
                        @include breakpoint("medium"){
                            margin: 0 auto;
                        }
                        .button {
                            width: 25px;
                            height: 25px;
                            color: #000;
                            position: unset;
                            font-size: 18px;
                            font-weight: 500;
                            background-color: #EBF2F0;
                            font-family: 'pp-neue-montreal-medium', Arial, Helvetica, sans-serif;
                            &[data-action="dec"] {
                                font-size: 0;
                                border-top-left-radius: 50px;
                                border-bottom-left-radius: 50px;
                                background: url('data:image/svg+xml,<svg width="7" height="2" viewBox="0 0 7 2" fill="none" xmlns="http://www.w3.org/2000/svg"><path d="M0 0H7V1.5H0V0Z" fill="%230F0F0F"/></svg>');
                                background-size: 7px;
                                background-position: center;
                                background-repeat: no-repeat;
                            }
                            &[data-action="inc"] {
                                font-size: 0;
                                background: url('data:image/svg+xml,<svg width="7" height="7" viewBox="0 0 7 7" fill="none" xmlns="http://www.w3.org/2000/svg"><path d="M2.79393 4.18816H0V2.81184H2.79393V0H4.20607V2.81184H7V4.18816H4.20607V7H2.79393V4.18816Z" fill="%230F0F0F"/></svg>');
                                background-size: 7px;
                                background-position: center;
                                background-repeat: no-repeat;
                            }
                            &:before, &:after{
                                display: none;
                            }
                        }
                        .form-input--incrementTotal {
                            padding: 0;
                            width: 25px;
                            height: 25px;
                            color: #000;
                            font-size: 14px;
                            font-weight: 500;
                            text-align: center;
                            background-color: #EBF2F0;
