.cart-cross-sell-wrap{
    overflow: hidden;
    margin: 30px 0 0px;
    position: relative;
    @include breakpoint(medium){
        margin: 80px 0 0px;
    }
h3{
    color: #000;
    font-size: 28px;
    font-weight: 500;
    margin: 0 0 32px;
    line-height: 30.8px;
    padding: 0 55px 0 0;
    font-family: 'pp-neue-montreal-medium', Arial, Helvetica, sans-serif;
    @include breakpoint(large){
        font-size: 32px;
        line-height: 35px;
    }
}
.cart-cross-sell{
    position: unset;
    display: flex;
    .slick-list {
        position: unset;
        padding: 0 5% 0 0 !important;
        @include breakpoint("small"){
            padding: 0 30% 0 0 !important;
        }
        @media(min-width:801px){
            padding: 0 10% 0 0 !important;
        }
        @include breakpoint("large"){
            padding:0 20% 0 0 !important;
        }
    }
    .slick-arrow {
        position: absolute;
        top: 0;
        width: 16px;
        height: 32px;
        &:hover {
            opacity: 0.6;
        }
        &.slick-next {
            right: 0;
            top: 20px;
            &:before {
                background-image: url('data:image/svg+xml,<svg width="13" height="24" viewBox="0 0 13 24" fill="none" xmlns="http://www.w3.org/2000/svg"><path d="M1.78296 2L11.4604 12L1.78296 22" stroke="%230F0F0F" stroke-width="2" stroke-linecap="square"/></svg>');
                background-size: 16px;
            }
        }
        &.slick-prev {
            top: 20px;
            right: 40px;
            left: inherit;
            &:before {
                background-image: url('data:image/svg+xml,<svg width="14" height="24" viewBox="0 0 14 24" fill="none" xmlns="http://www.w3.org/2000/svg"><path d="M11.783 22L2.10554 12L11.783 2" stroke="%230F0F0F" stroke-width="2" stroke-linecap="square"/></svg>');
                background-size: 16px;
            }
        }
        &:before {
            height: 100%;
            width: 100%;
        }
    }
    .cross-sell-item {
        display: flex;
        gap: 12px;
        flex-wrap: nowrap;
        padding: 0 15px 0 0;
        justify-content: space-between;
        @include breakpoint("small"){
            padding: 0 30px 0 0;
        }
        @include breakpoint("large"){
            padding: 0 60px 0 0px;
        }
        &:not(.slick-slide) {
            display: none;
            &:nth-child(1),
            &:nth-child(2) {
                display: block;
                width: 50%;   
            }
        }
        .item-figure {
            width: 116px;
            height: 116px;
            display: flex;
            justify-content: center;
            align-items: center;
            background-color: #F2F2F2;
            border-radius: 4px;
            padding: 10px;
            img {
                width: 100%;
                object-fit: cover;
                object-position: center;
            }
        }
        .item-body {
            display: flex;
            gap: 20px;
            flex-direction: column;
            width: calc(100% - 116px);
            justify-content: space-between;
            .item-title {
                color: #000;
                font-size: 16px;
                font-weight: 500;
                margin: 0 0 4px;
                line-height: 22px;
                font-family: 'pp-neue-montreal-medium', Arial, Helvetica, sans-serif;
                a{
                    color: #000;
                    font-size: 16px;
                    font-weight: 500;
                    margin: 0 0 4px;
                    line-height: 22px;
                    font-family: 'pp-neue-montreal-medium', Arial, Helvetica, sans-serif;
                    word-wrap: break-word;
                }
            }
            .item-intro {
                margin: 0;
                color: #000;
                opacity: 0.7;
                font-size: 14px;
                font-weight: 500;
                line-height: 22px;
                font-family: 'pp-neue-montreal-medium', Arial, Helvetica, sans-serif;
            }
            .item-add {
                display: flex;
                gap: 0 15px;
                flex-wrap: wrap;
                align-items: center;
                justify-content: flex-start;
                .item-price {
                    color: #000;
                    font-size: 16px;
                    font-weight: 500;
                    margin: 0;
                    line-height: 22px;
                    font-family: 'pp-neue-montreal-medium', Arial, Helvetica, sans-serif;
                }
                .item-add-cart {
                    color: #000;
                    font-size: 14px;
                    font-weight: 500;
                    line-height: 16px;
                    padding: 5px 15px;
                    border-radius: 50px;
                    transition: .2s all ease;
                    background-color: #EBF2F0;
                    border: 1px solid #EBF2F0;
                    font-family: 'pp-neue-montreal-medium', Arial, Helvetica, sans-serif;
                    &:hover{
                        color: #fff;
                        transition: .2s all ease;
                        background-color: #1D4739;
                    }
                }
            }
        }
    }
}
}

.page-type-cart .modal {
    border-radius: 20px;
    background-color: #fff;
    .modal-close {
        top: 24px;
        width: 48px;
        right: 24px;
        z-index: 50;
        height: 48px;
        display: flex;
        align-items: center;
        border-radius: 50px;
        background: #EBF2F0;
        justify-content: center;
        background-image: url('data:image/svg+xml,<svg width="8" height="8" viewBox="0 0 8 8" fill="none" xmlns="http://www.w3.org/2000/svg"><path d="M3.64238 5.12671L1.38455 7.38455L0.272309 6.27231L2.53014 4.01448L0.257833 1.74217L1.39902 0.600979L3.67133 2.87329L5.92916 0.615455L7.0414 1.72769L4.78357 3.98552L7.05588 6.25783L5.91469 7.39902L3.64238 5.12671Z" fill="%230F0F0F"></path></svg>');
        background-size: 12px;
        background-position: center;
        background-repeat: no-repeat;
        span{
            display: none;
        }
    }
    .modal-header{
        background-color: #fff;
        .modal-header-title {
            font-size: 32px;
            line-height: 35px;
            text-align: left;
            font-weight: 500;
            color: #000000;
            font-family: 'pp-neue-montreal-medium', Arial, Helvetica, sans-serif;
        }
    }
    .modal-body {
        @include breakpoint(medium){
            padding: 16px 50px;
        }
        form {
            display: flex;
            gap: 10px;
            flex-wrap: wrap;
            .form-field {
                width: 100%;
                margin-bottom: 0;
                @include breakpoint(medium){
                    width: calc(33.33% - 10px);
                }
                .form-label {
                    font-size: 14px;
                    color: #0F0F0F;
                    font-weight: 500;
                    margin-bottom: 12px;
                    line-height: 19.6px;
                    font-family: 'pp-neue-montreal-medium', Arial, Helvetica, sans-serif;
                    &--inlineSmall{
                        font-size: 16px;
                        line-height: 22px;
                    }
                    &--alternate{
                        font-size: 16px;
                        line-height: 22px;
                    }
                }
                .form-option-variant--color, .form-option-variant--pattern {
                    height: 38px;
                    width: 38px;
                }
            }
            .form-field > .form-checkbox + .form-label::after, .form-field > .form-radio + .form-label::after {
                box-shadow: 0 0 0 7px #EB6E38 inset;
            }
            .form-field[data-product-attribute="swatch"] .form-radio:checked + .form-option .form-option-variant {
                border: 3px solid #EB6E38;
            }
        }
        .form-actions {
            padding-top: 24px;
            margin-top: 24px !important;
            border-top: 1px solid #CFCFCF;
            @include breakpoint(small){
                padding-top: 32px;
                margin-top: 32px !important;
            }
        }
        .button--primary{
            color: #fff;
            border-radius: 100px;
            background: #1D4739;
            border-color: #1D4739;
            transition: .2s all ease;
            border: 1px solid #1D4739;
            &:hover{
                color: #1D4739;
                transition: .2s all ease;
                background-color: #fff;
            }
        }
    }
}
.cart-faq{
    margin: 60px 0 30px;
    @include breakpoint(medium){
        margin: 80px 0 0px;
    }
    h3{
        color: #000;
        font-size: 28px;
        font-weight: 500;
        margin: 0 0 32px;
        line-height: 30.8px;
        font-family: 'pp-neue-montreal-medium', Arial, Helvetica, sans-serif;
        @include breakpoint(large){
            font-size: 32px;
            line-height: 35px;
        }
    }
    .FAQBox {
        padding: 16px 0;
        border-bottom: 1px solid #CFCFCF;
        .CartFaqTitle {
            margin: 0;
            color: #000;
            cursor: pointer;
            font-size: 16px;
            font-weight: 500;
            line-height: inherit;
            position: relative;
            padding: 0px 16px 0 0;
            font-family: 'pp-neue-montreal-medium', Arial, Helvetica, sans-serif;
            &:before{
                content: "";
                position: absolute;
                background: url('data:image/svg+xml,<svg width="16" height="10" viewBox="0 0 16 10" fill="none" xmlns="http://www.w3.org/2000/svg"><path d="M14 2L8 8L2 2" stroke="%230F0F0F" stroke-width="2" stroke-linecap="square"/></svg>');
                right: 0;
                top: 50%;
                width: 16px;
                height: 10px;
                transition: .2s all ease;
                background-repeat: repeat;
                background-repeat: no-repeat;
                background-position: right center;
                transform: translateY(-50%) rotateX(0deg);
            }
            &.current{
                &:before{
                    transition: .2s all ease;
                    transform: translateY(-50%) rotateX(190deg);
                }
            }
        }
        .CartFaqParagraph {
            display: none;
            font-size: 14px;
            font-weight: 500;
            color: #4B4B4B;
            margin: 16px 0 0 0;
            line-height: 19.6px;
            font-family: 'pp-neue-montreal-medium', Arial, Helvetica, sans-serif;
        }
    }
}
.page-type-cart.header-layout-2.pace-running.usp-sticky.pace-done, .page-type-cart.header-layout-2.usp-sticky.pace-running, .page-type-cart.header-layout-2 {
    padding-top: 0px !important;
}
.page-type-page.header-layout-2.usp-sticky {
	padding-bottom: 0 !important;
}
