optimized-checkout.scss:
// =============================================================================
// Stencil Checkout - Customize the Optimized Single-Page Checkout experience
// =============================================================================

// ============================================================================= 
// 
// IMPORTANT DISCLAIMER  
// Please do not nest elements within class selectors, and do not use class selectors other than those given below.
// Future support is guaranteed only if class selectors' structure and naming are left unchanged.
//
// ============================================================================= 

//  
// Header Section
// Background, Logo Position #toggleButton
// -----------------------------------------------------------------------------
@font-face {
    font-family: 'pp-neue-montreal-regular';
    font-style: normal;
    font-weight: normal;
    src: url('https://ofinto.ch/content/fonts/PPNeueMontreal.woff') format('woff');
	font-display: swap;
}
@font-face {
    font-family: 'pp-neue-montreal-medium', Arial, Helvetica, sans-serif;
    font-style: normal;
    font-weight: normal;
    src: url('https://ofinto.ch/content/fonts/PPNeueMontreal-Medium.woff') format('woff');
	font-display: swap;
}

ul,
ol,
dl,
p {
    font-size: 13px;
    letter-spacing: 0;
}
.button.button--tertiary {
	border-radius: 0;
}

*:focus {
    outline-color: stencilColor("optimizedCheckout-colorFocus");
}

.optimizedCheckout-header {
    background-color: stencilColor("optimizedCheckout-header-backgroundColor");
    background-image: url(stencilImage('optimizedCheckout-backgroundImage', 'optimizedCheckout-backgroundImage-size'));
    background-size: cover;
    border-color: #cccccc;
    color: stencilColor("optimizedCheckout-header-textColor");
    //
    // TODO:
    // Configure media query to be 'desktop' breakpoint size
    // -----------------------------------------------------------------------------
    @media (min-width: 801px) {
        text-align: stencilString("optimizedCheckout-logo-position");
    }
    .checkoutHeader-logo {
        max-height: 30px;
    }
}
.checkoutHeader {
	width: 100%;
	margin: 0 auto;
	min-height: 88px;
	position: sticky;
	top: 0; 
	z-index: 999;
}
.checkoutHeader-content {
    width: 100%;
    display: flex;
    flex-wrap: wrap;
    max-width: 1440px;
    margin: 0 auto 0 0;
    align-items: center;
    padding: 24px 0px 20px 0px;
    justify-content: space-between;
    @media (min-width: 551px) {
        flex-wrap: inherit;
        justify-content: flex-start;
    }
    .secure-checkout{
        margin: 0;
        width: 160px;
        display: block;
        font-size: 12px;
        color: #0F0F0F;
        text-align: right;
        background-image: url('data:image/svg+xml,<svg width="18" height="20" viewBox="0 0 18 20" fill="none" xmlns="http://www.w3.org/2000/svg"><path d="M6.03931 9.75987L8.23397 11.8512L11.9649 8.2959" stroke="black" stroke-width="1.5" stroke-linecap="square"/><path d="M0.884948 5.40952C0.894995 5.18476 0.906951 4.99323 0.918019 4.84119L9.00659 0.836875L17.0924 4.83984C17.1024 4.98951 17.1129 5.17719 17.1213 5.39702C17.1449 6.00936 17.1523 6.86712 17.0879 7.84465C16.9576 9.82028 16.539 12.1958 15.4423 14.037C14.3412 15.8857 12.6824 17.2025 11.2666 18.0642C10.5628 18.4925 9.9301 18.8016 9.4755 19.0028C9.28524 19.087 9.12678 19.1521 9.00807 19.1988C8.88638 19.1505 8.723 19.083 8.5265 18.9956C8.06366 18.7897 7.42051 18.4749 6.70698 18.0425C5.27035 17.172 3.59801 15.8547 2.50856 14.0355C1.42491 12.2259 1.0204 9.85456 0.902603 7.87038C0.844365 6.8894 0.857374 6.02638 0.884948 5.40952Z" stroke="black" stroke-width="1.5"/></svg>');
        padding: 0 0 0 20px;
        background-size: 16px;
        max-width: fit-content;
        background-repeat: no-repeat;
        background-position: left center;
        @media (min-width: 551px) {
            display: none;
        }
    }
    .checkoutHeader-heading{
        font-size: 16px;
        text-align: left;
        width: calc(100% - 160px);
        @media (min-width: 551px) {
            width: auto;
        }
    }
}

//
// Primary and Secondary Headings
// Text Color & Typography
// .optimizedCheckout-headingPrimary styles top-level headings.
// .optimizedCheckout-headingSecondary styles certain lower-level elements, such as descriptions of cart items.
// -----------------------------------------------------------------------------

.optimizedCheckout-headingPrimary {
    color: #0F0F0F;
    font-size: 28px;
    font-weight: 500;
    line-height: 35.2px;
    letter-spacing: -0.01em;
    font-family: 'pp-neue-montreal-medium', Arial, Helvetica, sans-serif;
    @media (min-width: 969px) {
        font-size: 32px;
    }
}
.optimizedCheckout-headingSecondary {
    color: stencilColor("optimizedCheckout-headingPrimary-color");
    font-family: stencilFontFamily("optimizedCheckout-headingPrimary-font"), Arial, Helvetica, sans-serif;
    font-weight: stencilFontWeight("optimizedCheckout-headingPrimary-font");
    letter-spacing: .02em;
    font-size: 16px;
    .cart-heading-subheader{
        display: none;
    }
}
//
// Body
// Background & Shipping Method Overlay
// -----------------------------------------------------------------------------

body {
    background-color: stencilColor("optimizedCheckout-body-backgroundColor");
    font-size: 13px;
    letter-spacing: .02em;
}

.optimizedCheckout-overlay {
    background-color: stencilColor("optimizedCheckout-body-backgroundColor");
    border-color: stencilColor("optimizedCheckout-step-borderColor");
    color: stencilColor("optimizedCheckout-contentPrimary-color");
}

//
// Primary & Secondary Content
// Text Color & Typography
// .optimizedCheckout-contentPrimary styles body text in the Order Summary/Order Confirmation; drop-down-list items; and text that summarizes completed steps.
// .optimizedCheckout-contentSecondary styles check boxes' text labels, and lower-level text in the Order Summary.
// -----------------------------------------------------------------------------

.optimizedCheckout-contentPrimary {
    color: stencilColor("optimizedCheckout-contentPrimary-color");
    font-family: stencilFontFamily("optimizedCheckout-contentPrimary-font"), Arial, Helvetica, sans-serif;
    font-weight: stencilFontWeight("optimizedCheckout-contentPrimary-font");
    line-height: 22px;
}

.optimizedCheckout-contentSecondary {
    color: stencilColor("optimizedCheckout-contentSecondary-color");
    font-family: stencilFontFamily("optimizedCheckout-contentPrimary-font"), Arial, Helvetica, sans-serif;
    font-weight: stencilFontWeight("optimizedCheckout-contentSecondary-font");
    line-height: 22px;
}

.layout {
    max-width: 1440px;
    padding: 10px 0 0 0;
    @media (min-width: 551px) {
        padding: 0;
    }
}

.layout-main {
    padding: 35px 0 0 0;
    width: 100%;
    max-width: 100%;
    @media (min-width: 969px) {
        width: 61%;
        max-width: 857px;
        padding: 38px 10px 0 0;
    }
    .checkout-steps {
        padding-right: 0;
        padding-top: 0px;
        padding-bottom: 100px;
        @media (min-width: 969px) {
            padding-bottom: 0px;
        }
    }
}
.cart-header, .cart-modal-header {
    display: flex;
    padding: 32px 20px 13px 20px;
    gap: 8px;
    border-radius: 0px;
    position: relative;
    background: #ffffff;
    align-items: flex-start;
    justify-content: flex-start;
    flex-direction: column;
    @media (min-width: 552px) {
        padding: 32px 32px 22px 32px;
    }
    @media (min-width: 800px) {
        gap: 12px;
        flex-direction: column-reverse;
    }
    .cart-header-link, #cart-edit-link{
        float: right;
        font-size: 14px;
        font-weight: 500;
        color: #0F0F0F;
        background-image: url('data:image/svg+xml,<svg width="18" height="18" viewBox="0 0 18 18" fill="none" xmlns="http://www.w3.org/2000/svg"><g clip-path="url(%23clip0_909_385)"><path d="M4.5 14.6251H11.94C12.206 14.6252 12.4634 14.5312 12.6667 14.3595C12.8699 14.1879 13.0057 13.9498 13.05 13.6876L15 2.06255C15.0451 1.79512 15.1853 1.55298 15.3948 1.38072C15.6043 1.20845 15.8689 1.11766 16.14 1.12505H16.89" stroke="black" stroke-width="1.5" stroke-linecap="round" stroke-linejoin="round"/><path d="M5.85938 16.8828C5.70405 16.8828 5.57812 16.7569 5.57812 16.6016C5.57812 16.4462 5.70405 16.3203 5.85938 16.3203" stroke="black" stroke-width="1.5"/><path d="M5.85938 16.8828C6.01471 16.8828 6.14062 16.7569 6.14062 16.6016C6.14062 16.4462 6.01471 16.3203 5.85938 16.3203" stroke="black" stroke-width="1.5"/><path d="M11.5391 16.8828C11.3837 16.8828 11.2578 16.7569 11.2578 16.6016C11.2578 16.4462 11.3837 16.3203 11.5391 16.3203" stroke="black" stroke-width="1.5"/><path d="M11.5391 16.8828C11.6944 16.8828 11.8203 16.7569 11.8203 16.6016C11.8203 16.4462 11.6944 16.3203 11.5391 16.3203" stroke="black" stroke-width="1.5"/><path d="M13.4994 11.25H4.40944C3.91181 11.2451 3.42987 11.0753 3.03903 10.7672C2.64819 10.4591 2.37051 10.0302 2.24944 9.5475L1.13944 5.1975C1.12057 5.11469 1.12019 5.02874 1.13834 4.94578C1.15649 4.8628 1.19272 4.78486 1.24444 4.7175C1.29684 4.65002 1.36392 4.59537 1.44059 4.55768C1.51726 4.52 1.6015 4.50027 1.68694 4.5H14.6094" stroke="black" stroke-width="1.5" stroke-linecap="round" stroke-linejoin="round"/></g><defs><clipPath id="clip0_909_385"><rect width="18" height="18" fill="white"/></clipPath></defs></svg>');
        position: relative;
        padding: 0 0 0 44px;
        text-decoration: none;
        background-size: 20px;
        display: inline-block;
        background-repeat: no-repeat;
        background-position: left 16px center;
            font-family: 'pp-neue-montreal-medium', Arial, Helvetica, sans-serif;
        &#cart-print-link {
            background-image: none;
        }
        &::before{
            content: "";
            background-image: url('data:image/svg+xml,<svg width="8" height="12" viewBox="0 0 8 12" fill="none" xmlns="http://www.w3.org/2000/svg"><path d="M6 10L2 6L6 2" stroke="%230F0F0F" stroke-width="2" stroke-linecap="square"/></svg>');
            position: absolute;
            left: 0;
            top: 50%;
            width: 12px;
            height: 12px;
            transform: translateY(-50%);
            background-repeat: no-repeat;
            background-position: left center;
        }
    }
    .cart-title{
        font-size: 32px;
        color: #0F0F0F;
        font-weight: 500;
        line-height: 35.2px;
        font-family:  'pp-neue-montreal-medium', Arial, Helvetica, sans-serif;;
    }
    .cart-modal-close{
        position: absolute;
        top: 10px;
        right: 10px;
    }
}
.layout.optimizedCheckout-contentPrimary > div {
    @media (min-width: 968px) {
    	display: flex;
    	justify-content: space-between;
    	align-items: flex-start;
    }
}
.layout-cart {
    z-index: 2;
    padding-left: 0;
    padding-right: 0;
    position: relative;
    border-radius: 0px;
    background-color: #fff;
    border-left: 1px solid #ccc;
    border-right: 1px solid #ccc;
    border-bottom: 1px solid #ccc;
    @media (min-width: 968px) {
        width: 39%;
        float: right;
        position: sticky;
    	top: 88px;
        max-width: 402px;
		height: 100%;
		min-height: 100vw;
		max-height: 100vh;
    }
    .cart {
        margin-top: 0;
        border-radius: 0px;
    }
    .cart-section {
        padding: 18px 32px 20px 32px;
        &.optimizedCheckout-orderSummary-cartSection {
            padding-bottom: 27px;
        }
        &.optimizedCheckout-orderSummary-cartSection {
            padding-top: 10px;
            padding-bottom: 10px;
        }
        .cart-section-heading {
            text-transform: lowercase;
            margin-bottom: 15px;
            font-size: 13px;
        }
    }
}

.cart-actions {
    margin-top: 35px;
    margin-bottom: 15px;

    .button {
        min-width: 180px;
        background-color: stencilColor("optimizedCheckout-buttonPrimary-backgroundColor");
        border-color: stencilColor("optimizedCheckout-buttonPrimary-borderColor");
        color: stencilColor("optimizedCheckout-buttonPrimary-color");
        text-transform: uppercase;
        svg {
            fill: stencilColor("optimizedCheckout-buttonPrimary-color");
        }
        &:focus, &:hover, &:active {
            background-color: stencilColor("optimizedCheckout-buttonPrimary-backgroundColorHover");
            border-color: stencilColor("optimizedCheckout-buttonPrimary-borderColorHover");
            color: stencilColor("optimizedCheckout-buttonPrimary-colorHover");
            svg {
                fill: stencilColor("optimizedCheckout-buttonPrimary-colorHover");
            }
        }
    }
}
.customerView {
    display: inline-block;
    width: 100%;
    margin-bottom: 15px;
}
.customerView-body {
    padding-top: 0;
    font-weight: 400;
    width: 100%;
}
.customerView-actions {
    margin-top: 0;
}
.productList-item {
    margin-bottom: 16px;
    &:last-child{
        margin-bottom: 0px;
    }
    @media (min-width: 551px) {
        margin-bottom: 16px;
    }
    .product{
        gap: 12px;
        flex-wrap: nowrap;
    }
    &:last-child {
        margin-bottom: 15px;
    }
    .product-figure {
        padding-right: 0;
        border-radius: 10px;
        width: 80px;
        @media (min-width: 969px) {
            width: 26%;
        }
        img{
            border-radius: 10px;
            width: 80px;
            height: 80px;
            object-fit: contain;
            @media (min-width: 969px) {
                width: auto;
                height: auto;
            }
        }
    }
    .product-body{
        padding-right: 0;
        width: calc(80% - 104px);
        @media (min-width: 969px) {
            width: 50%;
        }
    }
    .product-actions{
        margin: 0;
        width: 20%;
        @media (min-width: 969px) {
            width: 25%;
        }
    }
    .product-options {
    	font-size: 12px;
    	line-height: 16.8px;
    	letter-spacing: .02em;
    	color: #000;
    	font-family: 'pp-neue-montreal-medium', Arial, Helvetica, sans-serif;
    	font-weight: normal;
    }
    .product-title {
    	font-size: 16px;
    	line-height: 20px;
    	font-weight: normal;
    	letter-spacing: .02em;
    	color: #000;
    	font-family: 'pp-neue-montreal-medium', Arial, Helvetica, sans-serif;
    	margin: 0 0 4px;
    }
    .product-price {
        font-size: 16px;
        font-weight: 500;
        color: #000;
        font-family: 'pp-neue-montreal-medium', Arial, Helvetica, sans-serif;
    }
}
.cart-priceItem {
    color: #000;
    font-size: 14px;
    font-weight: 500;
    margin: 0px 0 4px;
    line-height: 20px;
    letter-spacing: .02em;
    font-family: 'pp-neue-montreal-medium', Arial, Helvetica, sans-serif;
}
.cart-priceItem-value {
    color: #000;
    font-size: 14px;
    font-weight: 500;
    line-height: 20px;
    margin-bottom: 12px;
    letter-spacing: .02em;
    font-family: 'pp-neue-montreal-medium', Arial, Helvetica, sans-serif;
}
.cart-priceItem--total {
    margin-bottom: 0;
    .cart-priceItem-label {
        font-size: 20px;
        line-height: 24px;
        font-weight: 500;
    }
    .cart-priceItem-value {
        font-size: 20px;
        line-height: 24px;
        font-weight: 500;
    }
}
[data-test="cart-shipping"] {
    .cart-priceItem-value {
        color: #217556;
        font-weight: 500;
    }
}
.optimizedCheckout-orderSummary-cartSection {
	.redeemable-label {
		display: flex;
		justify-content: center;
		align-items: center;
		gap: 8px;
		&:before {
			content: "";
			background-image: url('data:image/svg+xml,<svg width="15" height="16" viewBox="0 0 15 16" fill="none" xmlns="http://www.w3.org/2000/svg"><g clip-path="url(%23clip0_909_425)"><path d="M13.1028 1.50049H9.26124C9.14184 1.5004 9.02724 1.54759 8.94251 1.63174L1.2625 9.30987C1.09438 9.4788 1 9.70747 1 9.9458C1 10.1842 1.09438 10.4128 1.2625 10.5818L4.91874 14.238C5.08774 14.4062 5.31644 14.5005 5.55484 14.5005C5.79324 14.5005 6.02194 14.4062 6.19094 14.238L13.8688 6.563C13.9529 6.47827 14.0001 6.36367 14 6.24423V2.40049C14.0006 2.28238 13.9778 2.16532 13.9329 2.05605C13.8881 1.94677 13.8221 1.84746 13.7387 1.76381C13.6553 1.68015 13.5562 1.61382 13.4471 1.56863C13.3379 1.52344 13.2209 1.50028 13.1028 1.50049Z" stroke="black" stroke-linecap="round" stroke-linejoin="round"/><path d="M11.5001 5.00086C11.3023 5.00086 11.109 4.94219 10.9445 4.83229C10.78 4.72242 10.6518 4.56622 10.5762 4.38346C10.5005 4.20072 10.4806 3.99966 10.5192 3.80563C10.5578 3.61163 10.6531 3.43343 10.793 3.29356C10.9328 3.1537 11.111 3.05845 11.305 3.01986C11.499 2.98127 11.7001 3.00107 11.8829 3.07677C12.0656 3.15247 12.2218 3.28065 12.3317 3.44512C12.4416 3.60958 12.5002 3.80294 12.5002 4.00076C12.5002 4.26599 12.3949 4.52036 12.2073 4.70792C12.0197 4.89549 11.7654 5.00086 11.5001 5.00086Z" fill="black"/></g><defs><clipPath id="clip0_909_425"><rect width="14" height="14.3333" fill="white" transform="translate(0.5 0.833328)"/></clipPath></defs></svg>');
			background-size: 16px;
			background-repeat: no-repeat;
			width: 16px;
			height: 20px;
		}
	}
}
.redeemable-label {
    z-index: 0;
    width: 100%;
    color: #0F0F0F;
    display: block;
    font-size: 14px;
    font-weight: 500;
    line-height: 20px;
    text-align: center;
    background: #EDEDED;
    position: relative;
    border-radius: 100px;
    text-decoration: none;
    margin: 0 0 12px auto !important;
    padding: 5px 25px;
    font-family: 'pp-neue-montreal-medium', Arial, Helvetica, sans-serif;
    &:hover, &:focus {
        color: stencilColor("optimizedCheckout-link-color");
    }
}
.redeemable-entry {
    margin: 0 0 12px 0 !important;
    .form-input {
        height: 42px;
        border-radius: 10px;
        @media (min-width: 551px) {
            width: calc(100% - 141px);
        }
    }
    .button {
        color: #fff;
        height: 42px;
        font-weight: 400;
        padding: 9px 15px;
        margin-left: 15px;
        text-transform: none;
        border-radius: 100px;
        border-color: #1D4739;
        background-color: #1D4739;
        font-family: 'pp-neue-montreal-medium', Arial, Helvetica, sans-serif;
        &:focus, &:hover, &:active {
            color:  #1D4739;;
            background-color: #fff;
            border-color:  #1D4739;
        }
        @media (min-width: 551px) {
            width: 115px;
            margin-left: 15px;
        }
    }
}

//
// Primary Button
// Background & Border & Text Color & Typography
// Styles the CONTINUE button for each step, & the final confirmation button.
// -----------------------------------------------------------------------------
.button {
    border-radius: 4px;
    padding: 13px 15px 12px 15px;
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
    font-size: 14px;
    text-transform: none;
    @media (min-width: 551px) {
        padding: 12px 25px;
    }
}
.customerView-actions .button {
	margin: 0;
	border: 0;
	padding: 0;
	color: #fff;
	font-size: 16px;
	font-weight: 500;
	text-decoration: none;
	background: transparent;
	background-image: linear-gradient(transparent 97%, #8bb192 3%);
	background-repeat: repeat-x;
	background-position-y: -2px;
	font-family: 'pp-neue-montreal-medium', Arial, Helvetica, sans-serif;
	&:hover{
    	color: #fff;
	    background-color: transparent;
	    background-image: linear-gradient(transparent 97%, #f2ca80 3%);
	}
}
.optimizedCheckout-buttonPrimary {
    background-color: #1D4739;
    border-color: #1D4739;
    color: #fff;
    font-family: 'pp-neue-montreal-medium', Arial, Helvetica, sans-serif;
    font-weight: 500;
    border-radius: 50px;
    &:focus, &:hover, &:active {
        background-color: stencilColor("optimizedCheckout-buttonPrimary-backgroundColorHover");
        border-color: stencilColor("optimizedCheckout-buttonPrimary-borderColorHover");
        color: stencilColor("optimizedCheckout-buttonPrimary-colorHover");
    }
    &[disabled], &[disabled]:hover, &[disabled]:focus, &[disabled].is-active, &[disabled].is-loading, &[disabled].active {
        background-color: stencilColor("optimizedCheckout-buttonPrimary-backgroundColorDisabled");
        border-color: stencilColor("optimizedCheckout-buttonPrimary-borderColorDisabled");
        color: stencilColor("optimizedCheckout-buttonPrimary-colorDisabled");
    }
}

//
// Secondary Button
// Background & Border & Text Color & Typography
// Styles the EDIT button for each step.
// -----------------------------------------------------------------------------

.optimizedCheckout-buttonSecondary {
    background-color: #fff;
    border-color: #fff;
    color: #0F0F0F;
    font-weight: 500;
    font-family: 'pp-neue-montreal-medium', Arial, Helvetica, sans-serif;
    letter-spacing: .02em;
    border-radius: 50px;
    &:focus, &:hover, &:active &:focus {
        background-color: #cee0d7;
        border-color: stencilColor("optimizedCheckout-buttonSecondary-borderColorHover");
        color: stencilColor("optimizedCheckout-buttonSecondary-colorHover");
    }
    &[disabled], &[disabled]:hover, &[disabled]:focus, &[disabled].is-active, &[disabled].is-loading, &[disabled].active {
        background-color: stencilColor("optimizedCheckout-buttonPrimary-backgroundColorDisabled");
        border-color: stencilColor("optimizedCheckout-buttonPrimary-borderColorDisabled");
        color: stencilColor("optimizedCheckout-buttonPrimary-colorDisabled");
    }
}

//
// Links
// Text Color & Typography
// -----------------------------------------------------------------------------
a {
    color: #0F0F0FBF;
    text-decoration: underline;
    font-family: 'pp-neue-montreal-medium', Arial, Helvetica, sans-serif;
    font-weight: stencilFontWeight("optimizedCheckout-contentPrimary-font");
    &:focus, &:hover {
        color: stencilColor("optimizedCheckout-link-hoverColor");
    }
}

//
// Order Summary (Mobile Drawer & Modal)
// Background Color
// -----------------------------------------------------------------------------

.optimizedCheckout-orderSummary {
    margin: 0;
    border-bottom: 0;
    border-top: 0;
