//
// Form Labels
// Text Color
// Styles text labels on form fields.
// -----------------------------------------------------------------------------

.optimizedCheckout-form-label {
    color: #000000;
    font-size: 15px;
    font-weight: 500;
    line-height: 20px;
    margin-bottom: 3px;
    font-family: 'pp-neue-montreal-medium', Arial, Helvetica, sans-serif;
    >.optimizedCheckout-contentSecondary {
    	font-family: 'pp-neue-montreal-medium', Arial, Helvetica, sans-serif;
    	font-weight: normal;
    	margin-left: 4px;
    	color: #0F0F0F99;
    	font-size: 14px;
    	line-height: 19.6px;
    }
}
.form-field--error {
    .optimizedCheckout-form-label,
    .form-inlineMessage {
        color: stencilColor("optimizedCheckout-formField-errorColor");
    }
}

//
// Form Input
// Background, Border, Shadow and Text Color
// Styles form fields.
// -----------------------------------------------------------------------------

$optimizedCheckout-input-boxShadow: inset 0 1px 1px stencilColor("optimizedCheckout-formField-shadowColor");
$optimizedCheckout-input--focus-boxShadow: 0 0 3px transparentize(stencilColor("optimizedCheckout-colorFocus"),
0.4);
$optimizedCheckout-input--error-boxShadow: 0 0 3px transparentize(stencilColor("optimizedCheckout-formField-errorColor"),
0.4);
.optimizedCheckout-form-input,
.optimizedCheckout-form-select {
    background-color: stencilColor("optimizedCheckout-formField-backgroundColor");
    border-color: #92B2A2;
    box-shadow: $optimizedCheckout-input-boxShadow;
    color: stencilColor("optimizedCheckout-formField-textColor");
    &::placeholder {
        color: stencilColor("optimizedCheckout-formField-placeholderColor");
    }
    .form-field--error & {
        background-color: stencilColor("optimizedCheckout-formField-backgroundColor");
        border-color: stencilColor("optimizedCheckout-formField-errorColor");
        box-shadow: $optimizedCheckout-input--error-boxShadow, $optimizedCheckout-input-boxShadow;
    }
}

.optimizedCheckout-form-input:focus, .optimizedCheckout-form-select:focus, .optimizedCheckout-form-select:hover {
    background-color: stencilColor("optimizedCheckout-formField-backgroundColor");
    border-color: stencilColor("optimizedCheckout-colorFocus");
    box-shadow: $optimizedCheckout-input--focus-boxShadow, $optimizedCheckout-input-boxShadow;
}
.optimizedCheckout-form-label::before {
    .optimizedCheckout-form-radio+&, .optimizedCheckout-form-checkbox+& {
        background-color: stencilColor("optimizedCheckout-formField-backgroundColor");
        border-color: #92B2A2;
        box-shadow: $optimizedCheckout-input-boxShadow;
        opacity: 1;
    }
    .optimizedCheckout-form-radio:checked+&, .optimizedCheckout-form-checkbox:checked+& {
        background-color: stencilColor("optimizedCheckout-formField-inputControlColor");
        border-color: stencilColor("optimizedCheckout-formField-borderColor");
        box-shadow: none;
        border-radius: 4px;
        width: 14px;
        height: 14px;
    }
    .optimizedCheckout-form-radio:focus+&, .optimizedCheckout-form-checkbox:focus+& {
        border-color: stencilColor("optimizedCheckout-colorFocus");
        box-shadow: $optimizedCheckout-input--focus-boxShadow, $optimizedCheckout-input-boxShadow;
    }
    .optimizedCheckout-form-radio:focus:checked+&, .optimizedCheckout-form-checkbox:focus:checked+& {
        box-shadow: $optimizedCheckout-input--focus-boxShadow;
    }
}
.form-checkbox+.form-label::after {
    top: 0px;
    color: #92B2A2;
    left: 1px;
    background-image: none;
    width: 9px;
    height: 4px;
    position: absolute;
    top: 5px;
    left: 4px;
    border: 2.5px solid #92B2A2;
    border-top: none;
    border-right: none;
    -moz-transform: scale(1) rotate(-45deg) !important;
    -o-transform: scale(1) rotate(-45deg) !important;
    -ms-transform: scale(1) rotate(-45deg) !important;
    -webkit-transform: scale(1) rotate(-45deg) !important;
    transform: scale(1) rotate(-45deg) !important;
}

