.oc_steps {
    display: flex;
    gap: 20px;
    max-width: 884px;
    overflow-X: scroll;
    scrollbar-width: none;
    padding: 0 0px 0 20px;
    margin: 24px auto 60px auto;
    @media (min-width: 884px) {
        padding: 0 20px;
    }
	.oc_steps_inner {
    	width: 33.33%;
        flex: 0 0 268px;
        position: relative;
    	background: #EBF2F0;
    	border-radius: 10px;
    	padding: 24px 24px 24px 56px;
    	.oc_number {
        	position: absolute;
            top: 24px;
            left: 24px;
        	display: flex;
        	color: #fff;
        	width: 20px;
        	height: 20px;
        	font-size: 14px;
        	font-weight: 500;
        	text-align: center;
        	line-height: 19.6px;
        	background: #1D4739;
        	border-radius: 100px;
        	align-items: center;
            justify-content: center;
        	letter-spacing: 0.01em;
            font-family: 'pp-neue-montreal-medium', Arial, Helvetica, sans-serif;
        }
        .oc_title {
        	color: #0F0F0F;
        	font-size: 16px;
        	font-weight: 500;
        	text-align: left;
        	margin: 0 0 12px;
        	line-height: 22.4px;
        	letter-spacing: 0.01em;
            font-family: 'pp-neue-montreal-medium', Arial, Helvetica, sans-serif;
        }
        .oc_desc {
        	margin: 0;
        	color: #0F0F0F;
        	font-size: 14px;
        	font-weight: 500;
        	text-align: left;
        	line-height: 19.6px;
        	letter-spacing: 0.01em;
            font-family: 'pp-neue-montreal-medium', Arial, Helvetica, sans-serif;
        }
        &:nth-child(1){
        	background: #1D4739;
        	.oc_number {
            	color: #0F0F0F;
            	background: #FFFFFF;
            }
            .oc_title {
            	color: #fff;
            }
            .oc_desc {
            	color: #fff;
            }
        }
    }
}
