div[data-test="payment-instructions"] {
	background-color: #EBF2F0;
	border-radius: 10px;
	padding: 24px;
	margin-top: 40px;
}
.payment-details {
	display: flex;
	gap: 32px 10px;
	padding: 24px;
	flex-wrap: wrap;
	margin: 0px 0 60px;
	border-radius: 10px;
	background-color: #EBF2F0;
	justify-content: space-between;
	@media (min-width: 551px) {
	    margin: 0px 0 0 0;
	    flex-wrap: nowrap;
	}
	.details-inner {
	    width: 100%;
    	display: flex;
    	flex-direction: column;
    	justify-content: space-between;
	    @media (min-width: 551px) {
	        width: 27%;
	    }
    	h5 {
        	margin: 0;
        	color: #0F0F0F;
        	font-size: 20px;
        	font-weight: 500;
        	line-height: 22px;
        	letter-spacing: 0%;
        	font-family: 'pp-neue-montreal-medium', Arial, Helvetica, sans-serif;
        }
    	h6 {
        	margin: 0;
        	color: #0F0F0F;
        	font-size: 20px;
        	font-weight: 500;
        	line-height: 22px;
        	letter-spacing: 0%;
        	font-family: 'pp-neue-montreal-medium', Arial, Helvetica, sans-serif;
        }
        ul {
        	margin: 0;
        	padding: 0;
        	list-style: none;
        	li {
        	    color: #464847;
            	font-size: 16px;
            	font-weight: 500;
            	letter-spacing: 1%;
            	line-height: 22.4px;
            	font-family: 'pp-neue-montreal-medium', Arial, Helvetica, sans-serif;
            	span{
                	color: #0F0F0F;
            	}
            }
            & + ul{
                margin-top: 20px;
            }
        }
        img {
            margin: 0;
        	width: 100%;
        	display: block;
            max-width: 162px;
    	    @media (min-width: 551px) {
            	margin: 0 auto;
            	max-width: 118px;
    	    }
        }
        &:nth-child(1){
	        width: 100%;
    	    @media (min-width: 551px) {
    	        width: 33.33%;
    	    }
    	    @media (min-width: 801px) {
    	        width: 27%;
    	    }
        	h6 {
        	    display: none;
            	@media (min-width: 551px) {
            	    display: block;
            	}
            }
        }
        &:nth-child(2){
	        width: 100%;
    	    @media (min-width: 551px) {
                width: calc(66.66% - 130px);
    	    }
        }
        &:nth-child(3){
            width: 100%;
            justify-content: center;
    	    @media (min-width: 551px) {
                width: 130px;
    	    }
        	h6 {
        	    display: block;
        	    margin: 0 0 40px;
            	@media (min-width: 551px) {
        	        display: none;
            	}
            }
        }
    }
    &--moneyorder{
        .details-inner {
        	h5 {
            	margin: 0 0 16px;
            }
    	    &:nth-child(1){
    	        width: 100%;
    	    }
    	}
    }
}
