components/themevale/footer/footer-layou:
{{#if banners.bottom}}
    {{#if settings.data_tag_enabled}}
        <div class="banners" data-banner-location="bottom">
            {{#each (limit banners.bottom_metadata 1)}}
                <div class="banner" data-event-type="promotion" data-entity-id="{{this.id}}" data-name="{{this.banner-name}}" data-position="{{this.location}}" data-name="{{this.banner-name}}">
                    <div data-event-type="promotion-click">
                        {{{this.content}}}
                    </div>
                </div>
            {{/each}}
        </div>
    {{else}}
        <div class="banners" data-banner-location="bottom">
            <div class="banner">
                {{{limit banners.bottom 1}}}
            </div>
        </div>
    {{/if}}
{{/if}}

<footer class="footer footer-layout-2" role="contentinfo">
    <div class="footer-middle">
        <div class="container">
            <section class="footer-info">
                <article class="footer-info-col footer-dropdownmobile" data-section-type="storeInfo">
                    {{#if theme_settings.footer-logo '!=' ''}}
                        <div class="footer-logo">
                            <div class="footer-logo-item left">
                                <a href="/">
                                    <img class="lazyload" data-src="/product_images/uploaded_images/{{theme_settings.footer-logo}}" alt="{{settings.store_logo.title}}" title="{{settings.store_logo.title}}">
                                </a>
                            </div>
                        </div>
                    {{/if}}
                    {{> components/common/social-links}}
                    {{#or theme_settings.show_powered_by theme_settings.show_copyright_footer theme_settings.show_template_by}}
                        <div class="footer-copyright">
                            {{#if theme_settings.show_copyright_footer}}
                                <p class="powered-by">&copy; {{moment format="YYYY"}} {{settings.store_name}} ag.</p>
                            {{/if}}
                            {{#if theme_settings.show_powered_by}}
                                <p class="powered-by">Powered by <a href="https://www.bigcommerce.com?utm_source=merchant&amp;utm_medium=poweredbyBC" rel="nofollow">BigCommerce.</a></p>
                            {{/if}}
                            <!-- {{#if theme_settings.show_template_by}}
                                <p class="powered-by">BigCommerce Themes by <a href="https://www.themevale.com/" rel="nofollow">Themevale.com</a></p>
                            {{/if}} -->
                        </div>
                    {{/or}}
                </article>

                <article class="footer-info-col footer-info-col--small footer-dropdownmobile" data-section-type="footer-col-1">
                    {{#if theme_settings.footer-col-1-title '!=' ''}}
                    <h5 class="footer-info-heading">{{theme_settings.footer-col-1-title}}</h5>
