<script>
$(document).ready(function() {
    setTimeout(function(){
        $('#menu-mobile ul > .nav-item .navUser-action').on('click', function(event) {
        	event.preventDefault();
        	$('#menu-mobile .nav-item').removeClass('jsonNav-active');
        	$('body').removeClass('jsonNav-active');
        	$('#menu-mobile .nav-item .mega-menu-content').hide();
        	$(this).closest('#menu-mobile .nav-item').addClass('jsonNav-active');
        	$('body').addClass('jsonNav-active');
        	$(this).parent().siblings('#menu-mobile .nav-item .mega-menu-content').show();
        });
        $('#menu-mobile .mob-hd').on('click', function() {
    		$('.nav-item').removeClass('jsonNav-active');
    		$('body').removeClass('jsonNav-active');
        });
        $('.mobileMenu-toggle').on('click', function() {
            $('.nav-item').removeClass('jsonNav-active');
            $('body').removeClass('jsonNav-active');
        });

        $('#menuMobile .navUser-helpMenu').addClass('mega-menu-content');
    },1500);
});
</script>

