components/themevale/sidebar/side-custom:
{{#if theme_settings.show_sidebar_image}}
<div class="sidebarBlock" id="sideBannerImage">
	<div class="side-cms-content">
        {{#if theme_settings.sidebar_image_1 '!=' ''}}
    		<a class="cms-image" href="{{theme_settings.sidebar_image_link_1}}">
    			<div class="card-img-container">
    				{{> components/themevale/themevale-responsive-img
                        image=theme_settings.sidebar_image_1
                        alt=theme_settings.sidebar_image_1
                        class="card-image"
                        fallback_size=theme_settings.sidebar_image_size
                        lazyload=theme_settings.lazyload_mode
                    }}
                </div>
    		</a>
        {{/if}}
        {{#if theme_settings.sidebar_image_2 '!=' ''}}
            <a class="cms-image" href="{{theme_settings.sidebar_image_link_2}}">
                <div class="card-img-container">
                    {{> components/themevale/themevale-responsive-img
                        image=theme_settings.sidebar_image_2
                        alt=theme_settings.sidebar_image_2
                        class="card-image"
                        fallback_size=theme_settings.sidebar_image_size
                        lazyload=theme_settings.lazyload_mode
                    }}
                </div>
            </a>
        {{/if}}
	</div>
</div>
{{/if}}

components/faceted-search/facets/hierarc:
{{#if items}}
<div class="accordion-block">
    <div
        class="accordion-navigation toggleLink {{#unless ../start_collapsed }} is-open {{/unless}}"
        role="button"
        data-collapsible="#facetedSearch-content--{{#replace '&' (hyphenate facet) }}{{else}}{{hyphenate facet}}{{/replace}}">
        <h5 class="accordion-title">
            {{ title }}
        </h5>

        <div class="accordion-navigation-actions">
            {{#if selected}}
                <a href="{{ remove_url }}" class="facetedSearch-clearLink" data-faceted-search-facet>{{lang 'search.faceted.clear'}}</a>
            {{/if}}
            <svg class="icon accordion-indicator toggleLink-text toggleLink-text--off">
                <use xlink:href="#icon-add" />
            </svg>
            <svg class="icon accordion-indicator toggleLink-text toggleLink-text--on">
                <use xlink:href="#icon-remove" />
            </svg>
        </div>
    </div>

    <div id="facetedSearch-content--{{#replace '&' (hyphenate facet) }}{{else}}{{hyphenate facet}}{{/replace}}" class="accordion-content {{#unless ../start_collapsed }} is-open {{/unless}}">
        <ul id="facetedSearch-navList--{{#replace '&' (hyphenate facet) }}{{else}}{{hyphenate facet}}{{/replace}}" class="navList" data-facet="{{facet}}" data-has-more-results="{{ has_more_results }}">
            {{#each items}}
                <li class="navList-item">
                    <a
                        href="{{ url }}"
                        class="navList-action navList-action--checkbox {{#if selected }} is-selected {{/if}} {{#if has_sub_categories }} has-children {{/if}}"
                        rel="nofollow"
                        data-id="{{ id }}"
                        data-faceted-search-facet>
                        {{{ sanitize title }}}
                        {{#if ../show_product_counts}}
                            {{#if count}}
                                <span>({{ count }})</span>
                            {{/if}}
                        {{/if}}
                    </a>
                    {{#if children}}
                        {{> components/faceted-search/facets/hierarchy-children children}}
                    {{/if}}
                </li>
            {{/each}}
        </ul>

        {{#if show_more_toggle}}
            <a href="#facetedSearch-navList--{{#replace '&' (hyphenate facet) }}{{else}}{{hyphenate facet}}{{/replace}}" role="button" class="toggleLink">
                <span class="toggleLink-text toggleLink-text--off">{{lang 'search.faceted.show-more'}}</span>
            </a>
        {{/if}}
    </div>
</div>
{{/if}}

components/products/ratings:
{{#for 1 5}}
    {{#if ../this.rating '>=' $index}}
        <span class="icon icon--ratingFull">
            <svg>
                <use xlink:href="#icon-star" />
            </svg>
        </span>
    {{else}}
        <span class="icon icon--ratingEmpty">
            <svg>
                <use xlink:href="#icon-star" />
            </svg>
        </span>
    {{/if}}
{{/for}}
