components/themevale/footer/footer-layou:
{{#if banners.bottom}}
    {{#if settings.data_tag_enabled}}
        <div class="banners" data-banner-location="bottom">
            {{#each (limit banners.bottom_metadata 1)}}
                <div class="banner" data-event-type="promotion" data-entity-id="{{this.id}}" data-name="{{this.banner-name}}" data-position="{{this.location}}" data-name="{{this.banner-name}}">
                    <div data-event-type="promotion-click">
                        {{{this.content}}}
                    </div>
                </div>
            {{/each}}
        </div>
    {{else}}
        <div class="banners" data-banner-location="bottom">
            <div class="banner">
                {{{limit banners.bottom 1}}}
            </div>
        </div>
    {{/if}}
{{/if}}

<footer class="footer footer-layout-1" role="contentinfo">
    {{#if theme_settings.enable_footer_top}}
    <div class="footer-top">
        <div class="container">
            <div class="footer-top-inner">
                {{#if settings.show_newsletter_summary }}
                    <p class="newsletter-sub-heading">{{settings.newsletter_summary}}</p>
                {{/if}}
                <a class="button button--large" data-popup-newsletter>{{lang 'common.login'}}</a>
            </div>
        </div>
    </div>
    {{/if}}
    <div class="footer-middle">
        <div class="container">
            <div class="footer-logo{{#if theme_settings.footer-logo '==' ''}} no-logo{{/if}}">
                {{#if theme_settings.footer-logo '!=' ''}}
                    <div class="footer-logo-item left">
                        <a href="/">
                            <img class="lazyload" data-src="/product_images/uploaded_images/{{theme_settings.footer-logo}}" alt="{{settings.store_logo.title}}" title="{{settings.store_logo.title}}">
                        </a>
                    </div>
                {{/if}}
                <div class="footer-logo-item right">
                    <p>{{#if theme_settings.footer-middle-top-text '!=' ''}}{{theme_settings.footer-middle-top-text}}{{/if}} {{#if theme_settings.footer-middle-top-link-text '!=' ''}}<a href="{{theme_settings.footer-middle-top-link-url}}">{{theme_settings.footer-middle-top-link-text}}</a>{{/if}} &#10095;</p>
                </div>
            </div>
            <section class="footer-info">
                {{#if settings.address}}
                    <article class="footer-info-col footer-dropdownmobile" data-section-type="storeInfo">
                        <h5 class="footer-info-heading">{{settings.store_name}} ag</h5>
                        <div class="footer-info-list">
                            <address>{{nl2br settings.address}}</address>
                            {{#if settings.phone_number}}
                                <span class="phone-number info">{{lang 'footer.call_us'}} <a href="tel:{{settings.phone_number}}">{{settings.phone_number}}</a></span>
                            {{/if}}
                            {{#if theme_settings.store_owner_email '!=' ''}}
                                <span class="email info"><a href="mailto:{{theme_settings.store_owner_email}}">{{theme_settings.store_owner_email}}</a></span>
                            {{/if}}
<!-- Google Customer Reviews Badge  (Commented out since we will only use it once we have 50 reviews) -->
<!--                         <span></span>
                        <g:ratingbadge merchant_id=285749385></g:ratingbadge> -->
                        </div>
                        <span></span>
                        <div class='jdgm-verified-badge-wrapper'></div>
                    </article>
                {{/if}}

